const Header = (props) => {
	return(
    <div>
      <h1>{props.course}</h1>
    </div>	
  )
}

const Part = (props) => {
  return (
      <div> {props.name} {props.exercises} </div>
  )
}

const  Content = (props) => {
  return (
  <div>
    props.forEach(value => {
      <Part name={value.name} exercises={value.exercises} />
    }
    )
  </div>
  )
}

const Total = (props) => {
  return (
  <div>
    <p>Number of exercises {props.hours}</p>
  </div>
  )
}


const App = () => {
	const course = 'Half Stack application development'
  const part = [{
    name: 'Fundamentals of React',
    exercises: 10
  },
	{
    name: 'Using props to pass data',
    exercises: 7
  },
 	{
    name: 'State of a component',
    exercises: 14
  }]
  return (
    <div>
	  <Header course = {course} />
	  <Content part={parts}/>
	  <Total parts={parts}/>
    </div>
  )
}

export default App
